import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Bar } from 'react-chartjs-2';
import './Comparison.css';

const Comparison = ({ selectedState }) => {
  const [comparativeData, setComparativeData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedMetric, setSelectedMetric] = useState('employmentGenerated');
  const navigate = useNavigate();

  useEffect(() => {
    fetchComparativeData();
  }, [selectedState]);

  const fetchComparativeData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const response = await fetch(`/api/comparative/${encodeURIComponent(selectedState)}`);
      const result = await response.json();
      
      if (result.success) {
        setComparativeData(result.comparative);
      } else {
        setError(result.error || 'Failed to load comparative data');
      }
    } catch (err) {
      setError('Network error. Please try again.');
      console.error('Error fetching comparative data:', err);
    } finally {
      setLoading(false);
    }
  };

  const getMetricLabel = (metric) => {
    const labels = {
      employmentGenerated: '[translate:‡§∞‡•ã‡§ú‡§ó‡§æ‡§∞ ‡§Æ‡§ø‡§≤‡§æ] / Employment',
      workCompletionRate: '[translate:‡§ï‡§æ‡§Æ ‡§™‡•Ç‡§∞‡§æ] / Work Completion',
      womenParticipation: '[translate:‡§Æ‡§π‡§ø‡§≤‡§æ ‡§≠‡§æ‡§ó‡•Ä‡§¶‡§æ‡§∞‡•Ä] / Women Participation',
      avgDaysPerHousehold: '[translate:‡§î‡§∏‡§§ ‡§¶‡§ø‡§®] / Average Days'
    };
    return labels[metric] || metric;
  };

  const getChartData = () => {
    if (!comparativeData.length) return null;

    const colors = ['#4CAF50', '#2196F3', '#FF9800', '#E91E63', '#9C27B0', '#FF5722'];
    
    return {
      labels: comparativeData.map(item => item.district),
      datasets: [{
        label: getMetricLabel(selectedMetric),
        data: comparativeData.map(item => item[selectedMetric]),
        backgroundColor: colors.slice(0, comparativeData.length),
        borderColor: colors.slice(0, comparativeData.length),
        borderWidth: 2,
        borderRadius: 8,
      }]
    };
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false
      },
      tooltip: {
        backgroundColor: 'rgba(0,0,0,0.8)',
        callbacks: {
          label: function(context) {
            const suffix = selectedMetric.includes('Rate') || selectedMetric.includes('Participation') ? '%' : '';
            return `${context.parsed.y.toLocaleString('hi-IN')}${suffix}`;
          }
        }
      }
