import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import PerformanceChart from '../components/charts/PerformanceChart';
import './Dashboard.css';

const Dashboard = ({ selectedState, selectedDistrict }) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    if (!selectedDistrict) {
      navigate('/');
      return;
    }
    
    fetchPerformanceData();
  }, [selectedState, selectedDistrict, navigate]);

  const fetchPerformanceData = async () => {
    try {
      setLoading(true);
      setError(null);
      
      const response = await fetch(
        `/api/performance/${encodeURIComponent(selectedState)}/${encodeURIComponent(selectedDistrict)}`
      );
      
      if (!response.ok) {
        throw new Error('Failed to fetch performance data');
      }
      
      const result = await response.json();
      
      if (result.success) {
        setData(result.performance);
      } else {
        setError(result.error || 'Failed to load performance data');
      }
    } catch (err) {
      setError(err.message);
      console.error('Error fetching performance data:', err);
    } finally {
      setLoading(false);
    }
  };

  const getPerformanceIcon = (score) => {
    if (score >= 75) return 'üü¢';
    if (score >= 50) return 'üü°';
    return 'üî¥';
  };

  const getHindiStatus = (score) => {
    if (score >= 75) return '‡§¨‡§π‡•Å‡§§ ‡§Ö‡§ö‡•ç‡§õ‡§æ / Excellent';
    if (score >= 50) return '‡§†‡•Ä‡§ï ‡§π‡•à / Good';
    return '‡§∏‡•Å‡§ß‡§æ‡§∞ ‡§ö‡§æ‡§π‡§ø‡§è / Needs Improvement';
  };

  if (loading) {
    return (
      <div className="dashboard-page">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>‡§™‡•ç‡§∞‡§¶‡§∞‡•ç‡§∂‡§® ‡§°‡•á‡§ü‡§æ ‡§≤‡•ã‡§° ‡§π‡•ã ‡§∞‡§π‡§æ ‡§π‡•à... / Loading performance data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="dashboard-page">
        <div className="error-container">
          <h2>‚ö†Ô∏è ‡§°‡•á‡§ü‡§æ ‡§≤‡•ã‡§° ‡§®‡§π‡•Ä‡§Ç ‡§π‡•ã ‡§∏‡§ï‡§æ / Failed to Load Data</h2>
          <p>Error: {error}</p>
          <button onClick={fetchPerformanceData} className="retry-button">
            üîÑ ‡§´‡§ø‡§∞ ‡§∏‡•á ‡§ï‡•ã‡§∂‡§ø‡§∂ ‡§ï‡§∞‡•á‡§Ç / Try Again
          </button>
          <button onClick={() => navigate('/')} className="back-button">
            ‚¨ÖÔ∏è ‡§µ‡§æ‡§™‡§∏ ‡§ú‡§æ‡§è‡§Ç / Go Back
          </button>
        </div>
      </div>
    );
  }

  if (!data) {
    return (
      <div className="dashboard-page">
        <div className="error-container">
          <h2>üìä ‡§ï‡•ã‡§à ‡§°‡•á‡§ü‡§æ ‡§â‡§™‡§≤‡§¨‡•ç‡§ß ‡§®‡§π‡•Ä‡§Ç / No Data Available</h2>
          <button onClick={() => navigate('/')} className="back-button">
            ‚¨ÖÔ∏è ‡§ú‡§ø‡§≤‡§æ ‡§¨‡§¶‡§≤‡•á‡§Ç / Change District
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-page">
      <div className="dashboard-header">
        <button onClick={() => navigate('/')} className="back-button">
          ‚¨ÖÔ∏è ‡§µ‡§æ‡§™‡§∏ / Back
        </button>
        <div className="header-info">
          <h1>üìä {selectedDistrict} ‡§ï‡§æ ‡§™‡•ç‡§∞‡§¶‡§∞‡•ç‡§∂‡§® / Performance</h1>
          <p className="state-info">{selectedState}</p>
          <p className="last-updated">
            ‡§Ö‡§Ç‡§§‡§ø‡§Æ ‡§Ö‡§™‡§°‡•á‡§ü / Last Updated: {new Date().toLocaleDateString('hi-IN')}
          </p>
        </div>
      </div>
// Add this after the dashboard-header div, before performance-dashboard
<div className="data-source-indicator">
  <div className="real-data-badge">
    <span className="badge-icon">‚úÖ</span>
    <div className="badge-content">
      <h4>‡§µ‡§æ‡§∏‡•ç‡§§‡§µ‡§ø‡§ï ‡§°‡•á‡§ü‡§æ / Real Data</h4>
      <p>Official MGNREGA Statistics</p>
      <p>‡§Ü‡§ß‡§ø‡§ï‡§æ‡§∞‡§ø‡§ï MGNREGA ‡§Ü‡§Ç‡§ï‡§°‡§º‡•á</p>
    </div>
  </div>
</div>

      {/* Key Metrics Cards */}
      <div className="performance-dashboard">
        {/* Employment Generation */}
        <div className="metric-card employment">
          <div className="metric-header">
            <span className="icon">üë•</span>
            <div>
              <h3>‡§∞‡•ã‡§ú‡§ó‡§æ‡§∞ ‡§Æ‡§ø‡§≤‡§æ</h3>
              <p className="metric-subtitle">Employment Provided</p>
            </div>
          </div>
          <div className="metric-value">
            <span className="number">{data.employmentGenerated.toLocaleString('hi-IN')}</span>
            <span className="label">‡§ò‡§∞ / Households</span>
          </div>
          <div className="metric-status">
            {getPerformanceIcon(data.workCompletionRate)}
            <span>{getHindiStatus(data.workCompletionRate)}</span>
          </div>
        </div>

        {/* Work Completion */}
        <div className="metric-card work-completion">
          <div className="metric-header">
            <span className="icon">üèóÔ∏è</span>
            <div>
              <h3>‡§ï‡§æ‡§Æ ‡§™‡•Ç‡§∞‡§æ ‡§π‡•Å‡§Ü</h3>
              <p className="metric-subtitle">Work Completed</p>
            </div>
          </div>
          <div className="metric-value">
            <span className="number">{data.workCompletionRate}%</span>
          </div>
          <div className="progress-bar">
            <div 
              className="progress-fill" 
              style={{ width: `${data.workCompletionRate}%` }}
            ></div>
          </div>
        </div>

        {/* Average Days */}
        <div className="metric-card avg-days">
          <div className="metric-header">
            <span className="icon">üìÖ</span>
            <div>
              <h3>‡§î‡§∏‡§§ ‡§¶‡§ø‡§®</h3>
              <p className="metric-subtitle">Average Days</p>
            </div>
          </div>
          <div className="metric-value">
            <span className="number">{data.avgDaysPerHousehold}</span>
            <span className="label">‡§¶‡§ø‡§® ‡§™‡•ç‡§∞‡§§‡§ø ‡§ò‡§∞ / Days per Household</span>
          </div>
          <div className="comparison">
            <span>‡§≤‡§ï‡•ç‡§∑‡•ç‡§Ø / Target: 100 ‡§¶‡§ø‡§® / days</span>
            <div className="target-progress">
              <div 
                className="target-fill" 
                style={{ width: `${Math.min(100, data.avgDaysPerHousehold)}%` }}
              ></div>
            </div>
          </div>
        </div>

        {/* Women Participation */}
        <div className="metric-card women">
          <div className="metric-header">
            <span className="icon">üë©</span>
            <div>
              <h3>‡§Æ‡§π‡§ø‡§≤‡§æ ‡§≠‡§æ‡§ó‡•Ä‡§¶‡§æ‡§∞‡•Ä</h3>
              <p className="metric-subtitle">Women Participation</p>
            </div>
          </div>
          <div className="metric-value">
            <span className="number">{data.womenParticipation}%</span>
          </div>
          <div className="metric-status">
            {data.womenParticipation >= 50 ? 'üü¢ ‡§Ö‡§ö‡•ç‡§õ‡§æ / Good' : 'üü° ‡§¨‡§¢‡§º‡§æ‡§è‡§Ç / Increase'}
          </div>
        </div>
      </div>

      {/* Charts Section */}
      <PerformanceChart data={data} />

      {/* Summary Card */}
      <div className="summary-card">
        <h3>üìã ‡§∏‡§æ‡§∞‡§æ‡§Ç‡§∂ / Summary</h3>
        <div className="summary-content">
          <div className="summary-row">
            <span>üè†</span>
            <span><strong>{data.employmentGenerated.toLocaleString('hi-IN')}</strong> ‡§ò‡§∞‡•ã‡§Ç ‡§ï‡•ã ‡§∞‡•ã‡§ú‡§ó‡§æ‡§∞ / households employed</span>
          </div>
          <div className="summary-row">
            <span>üìä</span>
            <span>‡§ï‡•Å‡§≤ <strong>{data.personDaysGenerated.toLocaleString('hi-IN')}</strong> ‡§µ‡•ç‡§Ø‡§ï‡•ç‡§§‡§ø-‡§¶‡§ø‡§® ‡§ï‡§æ‡§Æ / person-days of work</span>
          </div>
          <div className="summary-row">
            <span>üë©‚Äçüè≠</span>
            <span><strong>{data.womenParticipation}%</strong> ‡§Æ‡§π‡§ø‡§≤‡§æ ‡§≠‡§æ‡§ó‡•Ä‡§¶‡§æ‡§∞‡•Ä / women participation</span>
          </div>
          <div className="summary-row">
            <span>üèóÔ∏è</span>
            <span><strong>{data.workCompletionRate}%</strong> ‡§ï‡§æ‡§Æ ‡§™‡•Ç‡§∞‡§æ / work completion</span>
          </div>
        </div>
        {data.summary && data.summary.note && (
          <div className="demo-note">
            <span className="demo-badge">üß™ Demo</span>
            <p>{data.summary.note}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
